package day4_if_else_if_else;

public class ConditionEqual {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x= 100;
		int y= 100;
		if (x>y){
			System.out.print("x is bigger");
		}
		else if (y>x) {
			System.out.print("y is bigger");
		}
		else {
			System.out.print("Equal");
		}
			}
}